var buffer = "";

//Part 1
var markerLength = 4;
markerLength = 14;
for(var i = markerLength; i < buffer.Length; i++)
{
    var chars = buffer.Skip(i).Take(14).ToList();
    var uniqueChars = chars.Distinct().ToList();
    if(chars.Count == uniqueChars.Count)
    {
        Console.WriteLine(i + markerLength);
        return;
    }
}
